import uuid from 'uuid';
// reducer function takes action and based on the action type 
const reducer = (state, action) => {
  switch (action.type) {
    case 'ADD_NOTE':      
      const newNote = {
        id: uuid.v4(),// uuid is lib which gives uniq
        text: action.payload
      };

      const addedNotes = [...state.notes, newNote];

      return {
        ...state,
        notes: addedNotes
      };
      /// filter with the note id to delete the particular note
    case 'DELETE_NOTE':
      const deletedNotes = state.notes.filter(
        note => note.id !== action.payload
      );

      return {
        ...state,
        notes: deletedNotes
      };
      ///return the object and spreading the occurance state and getting the payload(note) passed in the action
      /// updating the current note
    case 'SET_CURRENT_NOTE':
      return {
        ...state,
        currentNote: action.payload
      };
    case 'UPDATE_NOTE':
      const updatedNote = {
        ...state.currentNote,
        text: action.payload
      };

      const updatedNotesIndex = state.notes.findIndex(
        note => note.id === state.currentNote.id
      );

      const updatedNotes = [
        ...state.notes.slice(0, updatedNotesIndex),
        updatedNote,
        ...state.notes.slice(updatedNotesIndex + 1)
      ];

      return {
        currentNote: null,
        notes: updatedNotes
      };
    default:
      return state;
  }
}
export default reducer
